      if (err.errno == 13) {
        this.log && this.log(
          'Unable to listen to port `' + this.port + '` as your Node.js instance does not have root privileges.'
        );
        this.emit('connect_failed', err);
        this.socket.removeAllListeners();
        delete this.socket;
      } else {
        this.log && this.log('FlashPolicyFileServer received an error event:\n' + (err.message ? err.message : err));
      }
    });

    this.socket.on('timeout', () => {});
    this.socket.on('close', (err) => {
      err && this.log && this.log('Server closing due to an error: \n' + (err.message ? err.message : err));
    });

    this.compile();
  }

  listen(port, server, cb) {
    let callback;
    const args = Array.prototype.slice.call(arguments, 0);

    args.forEach((arg) => {
      const type = typeof arg;
      if (type === 'number') this.port = arg;
      if (type === 'function') callback = arg;
      if (type === 'object') this.server = arg;
    });

    if (this.server) {
      this.server['@'] = (socket) => {
        socket.once('data', (data) => {
          if (data && data[0] === 60 && data.toString() === '<policy-file-request/>\0' &&
              socket && (socket.readyState === 'open' || socket.readyState === 'writeOnly')) {
            try {
              socket.end(this.buffer);
            } catch (e) {}
          }
        });
      };
      this.server.on('connection', this.server['@']);
    }

