      this.socket.online = true;
      if (callback) {
        callback.call(this);
        callback = undefined;
      }
    });

    return this;
  }

  responder(socket) {
    if (socket && socket.readyState == 'open' && socket.end) {
      try {
        socket.end(this.buffer);
      } catch (e) {}
    }
  }

  compile() {
    const xml = [
      '<?xml version="1.0"?>',
      '<!DOCTYPE cross-domain-policy SYSTEM "http://www.macromedia.com/xml/dtds/cross-domain-policy.dtd">',
      '<cross-domain-policy>'
    ];

    this.origins.forEach((origin) => {
      const parts = origin.split(':');
      xml.push('<allow-access-from domain="' + parts[0] + '" to-ports="'+ parts[1] +'"/>');
    });

    xml.push('</cross-domain-policy>');
    this.buffer = new Buffer(xml.join(''), 'utf8');
    return this;
  }

  add() {
    const args = Array.prototype.slice.call(arguments, 0);
    let i = args.length;

    while (i--) {
      if (this.origins.indexOf(args[i]) >= 0) {
        args[i] = null;
      }
    }

    Array.prototype.push.apply(
      this.origins,
      args.filter((value) => !!value)
    );

    this.compile();
    return this;
  }

  remove(origin) {
    const position = this.origins.indexOf(origin);
    if (position > 0) {
      this.origins.splice(position,1);
      this.compile();
    }
    return this;
  }

  close() {
    this.socket.removeAllListeners();
    this.socket.close();
    return this;
  }
