    self.onError('websocket error', e);
  };
};

WS.prototype.write = function(packets){
  var self = this;
  this.writable = false;

  var total = packets.length;
  for (var i = 0, l = total; i < l; i++) {
    (function(packet) {
      parser.encodePacket(packet, self.supportsBinary, function(data) {
        if (!BrowserWebSocket) {
          var opts = {};
