class DataManURL {
  constructor(url, type, options) {
    var self = this;
    options = options || {};

    self.url = url;
    self._type = type;

    if (options.auth) {
      if (options.auth.indexOf(':') < 0)
        throw new Error('auth option should be of the form "username:password"');
      options.headers = options.headers || {};
      options.headers['Authorization'] = "Basic "+
        (new Buffer(options.auth, "ascii")).toString("base64");
      delete options.auth;
    }

    self.urlOpts = options;
  }

  getBuffer(callback) {
    var self = this;

    request(_.extend({
      url: self.url,
      method: "GET",
      encoding: null,
      jar: false
    }, self.urlOpts), Meteor.bindEnvironment(function(err, res, body) {
      if (err) {
        callback(err);
      } else {
        self._type = res.headers['content-type'];
        callback(null, body);
      }
    }, function(err) {
      callback(err);
    }));
  }

  getDataUri(callback) {
    var self = this;

    self.getBuffer(function (error, buffer) {
      if (error) {
        callback(error);
      } else {
        if (!self._type) {
          callback(new Error("DataMan.getDataUri couldn't get a contentType"));
        } else {
          var dataUri = "data:" + self._type + ";base64," + buffer.toString("base64");
          callback(null, dataUri);
        }
      }
    });
  }

  createReadStream() {
    var self = this;
    return request(_.extend({
      url: self.url,
      method: "GET"
    }, self.urlOpts));
  }

  size(callback) {
    var self = this;

    if (typeof self._size === "number") {
      callback(null, self._size);
      return;
    }

    self.getBuffer(function (error, buffer) {
      if (error) {
        callback(error);
      } else {
        self._size = buffer.length;
        callback(null, self._size);
      }
    });
  }

  type() {
    return this._type;
  }
