const express = require('express');
const http = require('http');
const socketIo = require('socket.io');
const app = express();
const server = http.createServer(app);
const io = socketIo(server);

app.use(express.static('public'));

const particles = [];
const particleCount = 100;
const width = 800;
const height = 600;
const radius = 5;

for (let i = 0; i < particleCount; i++) {
    particles.push({
        x: Math.random() * width,
        y: Math.random() * height,
        vx: (Math.random() - 0.5) * 2,
        vy: (Math.random() - 0.5) * 2,
        color: `hsl(${Math.random() * 360}, 100%, 50%)`
    });
}

function updateParticles() {
    particles.forEach(particle => {
        particle.x += particle.vx;
        particle.y += particle.vy;

        if (particle.x - radius < 0 || particle.x + radius > width) {
            particle.vx *= -1;
        }
        if (particle.y - radius < 0 || particle.y + radius > height) {
            particle.vy *= -1;
        }

        particles.forEach(other => {
            if (particle !== other) {
                const dx = particle.x - other.x;
                const dy = particle.y - other.y;
                const distance = Math.sqrt(dx * dx + dy * dy);

                if (distance < radius * 2) {
                    const angle = Math.atan2(dy, dx);
                    const targetX = particle.x + Math.cos(angle) * radius;
                    const targetY = particle.y + Math.sin(angle) * radius;

                    particle.x = targetX + (targetX - particle.x) * 0.5;
                    particle.y = targetY + (targetY - particle.y) * 0.5;
                }
            }
        });
    });
}

setInterval(() => {
    updateParticles();
    io.emit('updateParticles', particles);
}, 16);

io.on('connection', socket => {
    socket.emit('initParticles', particles);

    socket.on('disconnect', () => {
        console.log('Client disconnected');
    });
});

server.listen(3000, () => {
    console.log('Server started on port 3000');
});
