const express = require('express');
const cors = require('cors');
const axios = require('axios');

function setupExpressServer(port) {
  const app = express();

  // CORS configuration
  const corsOptions = {
    origin: '*',
    methods: 'GET',
    allowedHeaders: 'Content-Type,Authorization',
  };
  app.use(cors(corsOptions));

  // Route handler for Wikipedia summaries
  app.get('/wikipedia_summaries', async (req, res) => {
    const keyword = req.query.keyword;
    if (!keyword) {
      return res.status(400).json({ error: 'Keyword parameter is required' });
    }

    try {
      // Fetch Wikipedia links based on the keyword
      const searchResponse = await axios.get(`https://en.wikipedia.org/w/api.php`, {
        params: {
          action: 'query',
          list: 'search',
          srsearch: keyword,
          format: 'json',
        },
      });

      const pages = searchResponse.data.query.search.map(result => result.pageid);

      // Fetch summaries and categories for each page
      const summariesResponse = await axios.get(`https://en.wikipedia.org/w/api.php`, {
        params: {
          action: 'query',
          prop: 'extracts|categories',
          pageids: pages.join('|'),
          format: 'json',
          exintro: true,
          explaintext: true,
        },
      });

      const summaries = summariesResponse.data.query.pages;

      res.json(summaries);
    } catch (error) {
      console.error('Error fetching Wikipedia data:', error);
      res.status(500).json({ error: 'Failed to fetch Wikipedia data' });
    }
  });

  // Start the server
  app.listen(port, () => {
    console.log(`Server is running on http://localhost:${port}`);
  });
}

