function createServer() {
  const server = http.createServer((req, res) => {
    const url = new URL(req.url, `http://${req.headers.host}`);
    const pathname = url.pathname;

    switch (pathname) {
      case '/get':
        if (req.method === 'GET') {
          res.writeHead(200, { 'Content-Type': 'text/plain' });
          res.end('content');
        } else {
          res.writeHead(405, { 'Content-Type': 'text/plain' });
          res.end('Method Not Allowed');
        }
        break;
      case '/put':
        if (req.method === 'PUT') {
          let body = '';
          req.on('data', chunk => {
            body += chunk.toString();
          });
          req.on('end', () => {
            if (body === 'content') {
              res.writeHead(200, { 'Content-Type': 'text/plain' });
              res.end('success');
            } else {
              res.writeHead(400, { 'Content-Type': 'text/plain' });
              res.end('Invalid Data');
            }
          });
        } else {
          res.writeHead(405, { 'Content-Type': 'text/plain' });
          res.end('Method Not Allowed');
        }
        break;
      case '/proxy':
        if (req.method === 'PUT') {
          const options = {
            hostname: req.headers.host,
            port: 80,
            path: '/put',
            method: 'PUT'
          };
          const proxyReq = http.request(options, proxyRes => {
            proxyRes.pipe(res);
          });
          req.pipe(proxyReq);
        } else {
          res.writeHead(405, { 'Content-Type': 'text/plain' });
          res.end('Method Not Allowed');
        }
        break;
      case '/test':
        const getReq = http.get(`http://${req.headers.host}/get`, (getRes) => {
          const options = {
            hostname: req.headers.host,
            port: 80,
            path: '/proxy',
            method: 'PUT'
          };
          const proxyReq = http.request(options, proxyRes => {
            proxyRes.pipe(res);
          });
          getRes.pipe(proxyReq);
        });
        break;
      default:
        res.writeHead(404, { 'Content-Type': 'text/plain' });
        res.end('Not Found');
    }
  });

  return server;
}

