const http = require('http');
const fs = require('fs');
const path = require('path');
const send = require('send');
const marked = require('marked');

class Server {
  constructor(config) {
    this.config = config;
    this._http = null;
    this.sockets = [];
    this.path = path.resolve(config.path || process.cwd());
  }

  updateServer() {
    this._http = http.createServer((req, res) => {
      const filePath = path.join(this.path, req.url);
      const isDir = fs.statSync(filePath).isDirectory();

      if (isDir && req.url.endsWith('/')) {
        fs.readdir(filePath, (err, files) => {
          if (err) {
            res.writeHead(500, { 'Content-Type': 'text/plain' });
            res.end('Internal Server Error');
            return;
          }
          let list = '# Directory Listing\n\n';
          files.forEach(file => {
            list += `- [${file}](${path.join(req.url, file)})\n`;
          });
          res.writeHead(200, { 'Content-Type': 'text/html' });
          res.end(marked(list));
        });
      } else if (isDir) {
        res.writeHead(301, { Location: req.url + '/' });
        res.end();
      } else {
        send(req, filePath)
          .on('error', err => {
            res.writeHead(err.status, { 'Content-Type': 'text/plain' });
            res.end(err.message);
          })
          .pipe(res);
      }
    });

    this._http.on('connection', socket => {
      this.sockets.push(socket);
      socket.on('close', () => {
        const index = this.sockets.indexOf(socket);
        if (index !== -1) {
          this.sockets.splice(index, 1);
        }
      });
    });
  }

  start(callback) {
    if (!this._http) {
      this.updateServer();
    }
    this._http.listen(this.config.port, callback);
  }

  stop(callback) {
    if (this._http) {
      this.sockets.forEach(socket => socket.destroy());
      this._http.close(callback);
      this._http = null;
    }
  }
}

module.exports = Server;
