const http = require('http');
const fs = require('fs');
const path = require('path');
const mime = require('mime-types');

const DEFAULT_PORT = 90;
const STATIC_DIRS = ['../src', '../dist'];

let cachedFiles = {};

function createServer(port = DEFAULT_PORT) {
    // Cache static files
    STATIC_DIRS.forEach(dir => {
        fs.readdirSync(dir).forEach(file => {
            const filePath = path.join(dir, file);
            const stats = fs.statSync(filePath);
            if (stats.isFile()) {
                cachedFiles[file] = fs.readFileSync(filePath);
                console.log(`Loaded file: ${filePath}`);
            }
        });
    });

    const server = http.createServer((req, res) => {
        let url = req.url;

        // Virtual path redirection
        if (url.startsWith('/core')) {
            url = url.replace('/core', '/src/core');
        } else if (url.startsWith('/plugins')) {
            url = url.replace('/plugins', '/src/plugins');
        }

        const ext = path.extname(url);
        const contentType = mime.lookup(ext) || 'application/octet-stream';

        if (cachedFiles[url]) {
            res.setHeader('Content-Type', contentType);
            res.end(cachedFiles[url]);
        } else if (ext === '.js') {
            // Dynamic JavaScript file concatenation
            const jsFiles = STATIC_DIRS.map(dir => path.join(dir, url)).filter(fs.existsSync);
            if (jsFiles.length > 0) {
                const concatenatedJs = jsFiles.map(file => fs.readFileSync(file)).join('\n');
                res.setHeader('Content-Type', 'application/javascript');
                res.end(concatenatedJs);
            } else {
                res.statusCode = 404;
                res.end('File not found');
            }
        } else {
            // Binary file handling
            const filePath = STATIC_DIRS.find(dir => fs.existsSync(path.join(dir, url)));
            if (filePath) {
                const stream = fs.createReadStream(filePath);
                stream.pipe(res);
            } else {
                res.statusCode = 404;
                res.end('File not found');
            }
        }
    });

    server.listen(port, '127.0.0.1', () => {
        console.log(`Server running at http://127.0.0.1:${port}/`);
    });
}

// Check for command line argument
if (process.argv.length > 2) {
    const port = parseInt(process.argv[2], 10);
    if (!isNaN(port)) {
        createServer(port);
    } else {
        console.error('Invalid port number');
    }
} else {
    createServer();
}
