async function checkData(receive, password, commandHandler) {
    if (receive.data.length < 4) return; // Minimum size for code length

    const codeLength = receive.data.readUInt32BE(0);
    if (receive.data.length < 4 + codeLength) return; // Wait for full code

    const code = receive.data.slice(4, 4 + codeLength);
    const data = receive.data.slice(4 + codeLength);

    if (checkCode(data, password, code)) {
        const response = await commandHandler(data.toString(), code.toString());
        receive.socket.write(pack(response));
    } else {
        receive.socket.write(pack({ code: 'ERROR' }));
    }

    receive.data = receive.data.slice(4 + codeLength);
    checkData(receive, password, commandHandler); // Check for more data
}

function checkCode(data, password, code) {
    const timestamp = Math.floor(Date.now() / 1000);
    const hash = crypto.createHash('md5').update(`${data.toString()}${timestamp}${password}`).digest('hex');
    return hash === code.toString();
}

function pack(data) {
    const response = Buffer.from(JSON.stringify(data));
    const packet = Buffer.alloc(4 + response.length);
    packet.writeUInt32BE(response.length, 0);
    response.copy(packet, 4);
    return packet;
}
