const net = require('net');
const { WeakRef } = require('weak');

class DNodeServer {
  constructor(cons, opts) {
    this.cons = cons;
    this.opts = opts || {};
    this.server = net.createServer(this._handleConnection.bind(this));
    this.server.sessions = {};
  }

  _handleConnection(stream) {
    const sessionId = this._randomId();
    this.server.sessions[sessionId] = stream;

    const session = {
      id: sessionId,
      stream: this.opts.weak ? new WeakRef(stream) : stream
    };

    this.cons(session);

    stream.on('data', (data) => {
      this.emit('data', session, data);
    });

    stream.on('end', () => {
      delete this.server.sessions[sessionId];
      this.emit('end', session);
    });

    stream.on('error', (err) => {
      this.emit('error', session, err);
    });
  }

  listen(port, host, callback) {
    return this.server.listen(port, host, callback);
  }

  _randomId() {
    return Math.random().toString(16).substr(2, 16);
  }

  emit(eventName, ...args) {
    if (this.opts.proto && this.opts.proto[eventName]) {
      this.opts.proto[eventName].apply(null, args);
    }
  }
}

module.exports = DNodeServer;
