const net = require('net');
const tls = require('tls');

class IMAPServer {
  constructor(options) {
    this.options = options || {};
    this.server = net.createServer();
    this.connections = new Set();
    this._closeTimeout = null;
    this._setListeners();
  }

  connect(socket, socketOptions) {
    const connection = new IMAPConnection(socket, socketOptions);
    this.connections.add(connection);
    socket.on('close', () => this.connections.delete(connection));
  }

  listen(...args) {
    this.server.listen(...args);
  }

  close(callback) {
    this.server.close(() => {
      clearTimeout(this._closeTimeout);
      callback && callback();
    });
    this._closeTimeout = setTimeout(() => this.server.close(), 5000);
  }

  _setListeners() {
    this.server.on('listening', this._onListening.bind(this));
    this.server.on('close', this._onClose.bind(this));
    this.server.on('error', this._onError.bind(this));
  }

  _onListening() {
    console.log('IMAP Server listening on:', this.server.address());
  }

  _onClose() {
    console.log('IMAP Server closed');
  }

  _onError(err) {
    console.error('IMAP Server error:', err);
  }

  _handleProxy(socket, callback) {
    // Placeholder for proxy protocol handling
    callback(null, socket);
  }

  _upgrade(socket, callback) {
    if (this.options.tls) {
      const tlsOptions = Object.assign({}, this.options.tls, { socket });
      const secureSocket = tls.connect(tlsOptions, () => {
        callback(secureSocket);
      });
    } else {
      callback(socket);
    }
  }
}

class IMAPConnection {
  constructor(socket, options) {
    this.socket = socket;
    this.options = options;
    this._setupListeners();
  }

  _setupListeners() {
    this.socket.on('data', this._onData.bind(this));
    this.socket.on('end', this._onEnd.bind(this));
    this.socket.on('error', this._onError.bind(this));
