const net = require('net');

class RedisInterceptor {
  constructor(Pythagora, listenPort, targetPort = 6379, intermediateData = []) {
    this.Pythagora = Pythagora;
    this.listenPort = listenPort;
    this.targetPort = targetPort;
    this.intermediateData = intermediateData;
    this.listenSocket = null;
    this.targetSocket = null;
    this.mode = undefined; // Default mode is forward
  }

  async init() {
    return new Promise((resolve, reject) => {
      this.listenSocket = net.createServer((connection) => {
        connection.on('data', (data) => {
          this.forwardData(connection, data);
        });

        connection.on('end', () => {
          console.log('Client disconnected');
        });
      });

      this.listenSocket.listen(this.listenPort, () => {
        console.log(`Interceptor listening on port ${this.listenPort}`);
        resolve();
      });

      this.listenSocket.on('error', (err) => {
        reject(err);
      });
    });
  }

  forwardData(connection, data, saveData = false) {
    if (!this.targetSocket) {
      this.targetSocket = net.createConnection(this.targetPort, () => {
        this.targetSocket.write(data);
      });

      this.targetSocket.on('data', (response) => {
        if (this.mode === MODES.CAPTURE && saveData) {
          this.Pythagora.saveRequestResponsePair(data, response);
        } else if (this.mode === MODES.TEST) {
          const mockResponse = this.findMockResponse(data);
          if (mockResponse) {
            connection.write(mockResponse);
          } else {
            connection.write(response);
          }
        } else {
          connection.write(response);
        }

        this.targetSocket.end();
        this.targetSocket = null;
      });

      this.targetSocket.on('error', (err) => {
        console.error('Error connecting to Redis server:', err);
        connection.end();
      });
    }
  }

  splitIntoChunks(str, chunkSize) {
    const result = [];
    for (let i = 0; i < str.length; i += chunkSize) {
      result.push(str.slice(i, i + chunkSize));
    }
    return result;
  }

  findMockResponse(request) {
    for (const { request: mockRequest, response: mockResponse } of this.intermediateData) {
      if (mockRequest === request) {
        return mockResponse;
      }
    }
    return null;
  }
}

module.exports = RedisInterceptor;
