const EventEmitter = require('events');

class Node extends EventEmitter {
  constructor(id) {
    super();
    this.id_ = id;
    this.peers_ = {};
    this.PORT = 10000 + id; // Base port + node ID
    this.server_ = net.createServer((socket) => {
      socket.on('data', (data) => this.on_data(data, socket));
      socket.on('end', () => this.remove_peer(socket));
    });
  }

  async start() {
    return new Promise((resolve, reject) => {
      this.server_.listen(this.PORT, () => {
        console.log(`Node ${this.id_} listening on port ${this.PORT}`);
        this.connectToRandomPeers().then(resolve).catch(reject);
      });
    });
  }

  async connectToRandomPeers() {
    const maxPeers = 5;
    for (let i = 0; i < maxPeers; i++) {
      const randomId = Math.floor(Math.random() * 10); // Assuming nodes are numbered 0-9
      if (randomId !== this.id_ && !this.peers_[randomId]) {
        await this.connectToPeer(randomId);
      }
    }
  }

  async connectToPeer(id) {
    return new Promise((resolve, reject) => {
      const socket = net.createConnection({ port: 10000 + id }, () => {
        console.log(`Connected to node ${id}`);
        this.add_peer(socket, id);
        resolve();
      });

      socket.on('error', (err) => {
        console.error(`Failed to connect to node ${id}:`, err);
        reject(err);
      });
    });
  }

  on_data(data, socket) {
    const message = data.toString();
    console.log(`Received message from node ${this.getPeerId(socket)}: ${message}`);
    this.emit('message', message, this.getPeerId(socket));
  }

