    if (typeof socket === 'number') {
      socket = this.peers_[socket];
    }
    if (socket) {
      socket.write(data);
    }
  }

  broadcast(data) {
    for (const id in this.peers_) {
      this.send(parseInt(id), data);
    }
  }

  add_peer(socket, remote_id) {
    this.peers_[remote_id] = socket;
  }

  remove_peer(socket) {
    for (const id in this.peers_) {
      if (this.peers_[id] === socket) {
        delete this.peers_[id];
        break;
      }
    }
  }

  list_peers() {
    return Object.keys(this.peers_).map(Number);
  }

  getPeerId(socket) {
    for (const id in this.peers_) {
