const EventEmitter = require('events');

class StreamManager extends EventEmitter {
  constructor() {
    super();
    this.streams = {};
  }

  addMessage(streamId, message) {
    if (!this.streams[streamId]) {
      this.streams[streamId] = [];
    }
    this.streams[streamId].push(message);
    if (this.streams[streamId].length > 100) {
      this.streams[streamId].shift();
    }
    this.emit('update', streamId, message);
  }

  getStream(streamId) {
    return this.streams[streamId] || [];
  }
}

function setupUDPServer(argv, io, debug) {
  const udpServer = dgram.createSocket('udp4');
  const streamManager = new StreamManager();

  udpServer.on('message', (msg, rinfo) => {
    try {
      const message = JSON.parse(msg.toString());
      const { streamId, data } = message;
      streamManager.addMessage(streamId, data);
      io.emit('newMessage', { streamId, data });
      debug(`Received message from ${rinfo.address}:${rinfo.port}`);
    } catch (error) {
      debug(`Error parsing message: ${error.message}`);
    }
  });

