const dgram = require('dgram');
const url = require('url');

class UdpPoller {
  constructor(target, timeout, callback) {
    this.udpServer = dgram.createSocket('udp4');
    this.target = target;
    this.timeout = timeout;
    this.callback = callback;
    this.initialize();
  }

  static get type() {
    return 'udp';
  }

  static validateTarget(target) {
    try {
      const parsedUrl = new URL(target);
      return parsedUrl.protocol === 'udp:' || parsedUrl.protocol === 'udp4:';
    } catch (e) {
      return false;
    }
  }

  initialize() {
    const parsedUrl = new URL(this.target);
    this.targetAddress = parsedUrl.hostname;
    this.targetPort = parseInt(parsedUrl.port, 10);
  }

  poll() {
    const message = Buffer.from('ping');
    this.udpServer.send(message, 0, message.length, this.targetPort, this.targetAddress, (err) => {
      if (err) {
        this.callback(err, null);
      }
    });

    this.timeoutId = setTimeout(() => this.timeoutReached(), this.timeout);
    this.udpServer.on('message', this.onResponseCallback.bind(this));
  }

  onResponseCallback(message, sender) {
    clearTimeout(this.timeoutId);
    if (message.toString() === 'pong') {
      this.callback(null, { status: 'alive', sender });
    } else {
      this.callback(new Error('Unexpected response'), null);
    }
    this.udpServer.close();
  }

  timeoutReached() {
    this.udpServer.removeListener('message', this.onResponseCallback);
    this.callback(new Error('Timeout'), null);
    this.udpServer.close();
  }
}
