const dgram = require('dgram');
const EventEmitter = require('events');

class MasterIPC extends EventEmitter {
  constructor(options) {
    super();
    this.options = options;
    this._sock = null;
    this.children = [];
    this.sock = null;
  }

  IPCSocket() {
    if (!this._sock) {
      this._sock = dgram.createSocket('udp4');
      this._sock.on('message', (msg, rinfo) => {
        const { id, method, args } = JSON.parse(msg.toString());
        const worker = this.children.find(child => child.id === id);
        if (worker) {
          this.invoke(method, args, worker);
        }
      });
    }
    return this._sock;
  }

  setupIPC() {
    this.sock = this.IPCSocket();
    this.sock.bind(this.options.port, this.options.address, () => {
      this.emit('start');
    });
  }

  call(id, method, ...args) {
    const message = JSON.stringify({ id, method, args });
    this.sock.send(message, 0, message.length, this.options.port, this.options.address);
  }

  invoke(method, args, worker) {
    if (worker[method]) {
      worker[method](...args, worker);
    }
  }

  connect(worker) {
    this.children.push(worker);
    this.emit('worker connected', worker);
  }

  connectMaster(sig) {
    process.kill(process.pid, sig || 'SIGTERM');
  }

  masterKilled(data) {
    this.emit('restart', data);
  }
}

module.exports = MasterIPC;
