const EventEmitter = require('events');
const WebSocket = require('ws');

class Chrome extends EventEmitter {
  constructor(options = {}, notifier) {
    super();
    this.host = options.host || 'localhost';
    this.port = options.port || 9222;
    this.secure = options.secure || false;
    this.webSocketUrl = `${this.secure ? 'wss' : 'ws'}://${this.host}:${this.port}`;
    this._ws = null;
    this._notifier = notifier;
    this._callbacks = {};
    this._nextCommandId = 1;
  }

  async _start() {
    await this._connectToWebSocket();
  }

  async _connectToWebSocket() {
    return new Promise((resolve, reject) => {
      this._ws = new WebSocket(this.webSocketUrl);

      this._ws.on('open', () => {
        this.emit('connected');
        resolve();
      });

      this._ws.on('close', () => {
        this._handleConnectionClose();
      });

      this._ws.on('message', (message) => {
        this._handleMessage(message);
      });

      this._ws.on('error', (error) => {
        this.emit('error', error);
        reject(error);
      });
    });
  }

  _handleConnectionClose() {
    this.emit('disconnected');
    this._ws = null;
    this._callbacks = {};
  }

  _handleMessage(message) {
    try {
      const data = JSON.parse(message);
      if (data.id && this._callbacks[data.id]) {
        const callback = this._callbacks[data.id];
        delete this._callbacks[data.id];
        if (data.error) {
          callback(data.error);
        } else {
          callback(null, data.result);
        }
      } else if (data.method) {
        this.emit(data.method, data.params);
      }
    } catch (error) {
      this.emit('error', error);
    }
  }

  send(method, params = {}, sessionId = null, callback) {
    return new Promise((resolve, reject) => {
      const id = this._nextCommandId++;
      const message = {
        id,
        method,
        params,
      };
      if (sessionId) {
        message.sessionId = sessionId;
      }
      this._enqueueCommand(method, params, sessionId, (error, result) => {
        if (callback) {
          callback(error, result);
        }
        if (error) {
          reject(error);
        } else {
          resolve(result);
        }
      });
      this._ws.send(JSON.stringify(message));
    });
  }

  _enqueueCommand(method, params, sessionId, callback) {
    this._callbacks[this._nextCommandId - 1] = callback;
  }

  close(callback) {
    return new Promise((resolve, reject) => {
      if (this._ws) {
        this._ws.close(() => {
          this._handleConnectionClose();
          if (callback) {
            callback();
          }
          resolve();
        });
      } else {
        if (callback) {
          callback();
        }
        resolve();
      }
    });
  }
}

module.exports = Chrome;
