class WebSocketClient {
    constructor() {
        this.ws = null;
        this.executorMap = new Map();
        this.bufferMap = new Map();
        this.timer = null;
    }

    connect() {
        return new Promise((resolve, reject) => {
            this.ws = new WebSocket('wss://speech.platform.bing.com/speech/synthesize', {
                headers: {
                    'Ocp-Apim-Subscription-Key': 'YOUR_SUBSCRIPTION_KEY',
                    'Content-Type': 'application/ssml+xml'
                }
            });

            this.ws.on('open', () => {
                console.log('Connected to Bing Speech API');
                resolve(this.ws);
            });

            this.ws.on('message', (data, isBinary) => {
                if (isBinary) {
                    this.handleAudioData(data);
                } else {
                    const response = JSON.parse(data);
                    if (response.header.messageType === 'TurnEnd') {
                        const requestId = response.header.requestId;
                        const audioBuffer = this.bufferMap.get(requestId);
                        const { resolve, reject } = this.executorMap.get(requestId);
                        this.bufferMap.delete(requestId);
                        this.executorMap.delete(requestId);
                        resolve(audioBuffer);
                    }
                }
            });

            this.ws.on('error', (error) => {
                reject(error);
            });

            this.ws.on('close', () => {
                console.log('Connection closed');
            });
        });
    }

    sendRequest(ssml, format) {
        return new Promise((resolve, reject) => {
            if (!this.ws || this.ws.readyState !== WebSocket.OPEN) {
                reject(new Error('WebSocket is not open'));
                return;
            }

            const requestId = Date.now().toString();
            this.executorMap.set(requestId, { resolve, reject });
            this.bufferMap.set(requestId, Buffer.alloc(0));

            const message = JSON.stringify({
                "path": "/synthesize",
                "header": {
                    "method": "POST",
                    "requestId": requestId,
                    "contentType": "application/ssml+xml",
                    "contentLength": ssml.length,
                    "protocolVersion": "1.0",
                    "locale": "en-US",
                    "outputFormat": format,
                    "authorization": "Bearer YOUR_ACCESS_TOKEN"
                },
                "body": ssml
            });

            this.ws.send(message, (error) => {
                if (error) {
                    reject(error);
                }
            });
        });
    }

    handleAudioData(data) {
        const requestId = Object.keys(this.bufferMap)[0];
        const currentBuffer = this.bufferMap.get(requestId);
        this.bufferMap.set(requestId, Buffer.concat([currentBuffer, data]));
    }
}

