const EventEmitter = require('events');
const WebSocket = require('ws');

class Chrome extends EventEmitter {
  constructor(options = {}, notifier) {
    super();
    this.host = options.host || 'localhost';
    this.port = options.port || 9222;
    this.secure = options.secure || false;
    this._notifier = notifier;
    this._callbacks = {};
    this._nextCommandId = 1;
    this.webSocketUrl = `${this.secure ? 'wss' : 'ws'}://${this.host}:${this.port}`;
    this._ws = null;
  }

  async _connectToWebSocket() {
    return new Promise((resolve, reject) => {
      this._ws = new WebSocket(this.webSocketUrl);

      this._ws.on('open', () => {
        resolve();
      });

      this._ws.on('message', (data) => {
        this._handleMessage(JSON.parse(data));
      });

      this._ws.on('error', (error) => {
        reject(error);
      });

      this._ws.on('close', () => {
        this.emit('disconnected');
      });
    });
  }

  _handleMessage(message) {
    if (message.id && this._callbacks[message.id]) {
      const callback = this._callbacks[message.id];
      delete this._callbacks[message.id];
      if (message.error) {
        callback(message.error);
      } else {
        callback(null, message.result);
      }
    } else if (message.method) {
      this.emit(message.method, message.params);
    }
  }

  _enqueueCommand(method, params = {}, sessionId = '', callback) {
    const id = this._nextCommandId++;
    const command = { id, method, params };
    if (sessionId) {
      command.sessionId = sessionId;
    }
    this._ws.send(JSON.stringify(command));
    this._callbacks[id] = callback;
  }

  send(method, ...args) {
    const params = args.length > 0 && typeof args[args.length - 1] === 'function' ? args.pop() : {};
    const sessionId = args.length > 0 && typeof args[args.length - 1] === 'string' ? args.pop() : '';

    return new Promise((resolve, reject) => {
      this._enqueueCommand(method, params, sessionId, (error, result) => {
        if (error) {
          reject(error);
        } else {
          resolve(result);
        }
      });
    }).then((result) => {
      if (typeof args[args.length - 1] === 'function') {
        args[args.length - 1](null, result);
      }
      return result;
    }).catch((error) => {
      if (typeof args[args.length - 1] === 'function') {
        args[args.length - 1](error);
      }
      throw error;
    });
  }
}

