            linkDiv.querySelector(".status").textContent = "- Running...";
            window.open(linkDiv.querySelector(".testLink").href + "&autostart=true&subscriptionKey=" + subscriptionKey, "", "width=800, height=800, top=0, left=400")
            numRunning++;
        });
    }

