class RPCWebSocket {
    constructor(address, encryption = false, namespace = '') {
        this._namespace = namespace;
        this._listeners = {};
        this._handles = {};
        this._socket = null;
        this.setAddress(address, encryption);
    }

    setAddress(address, encryption) {
        const scheme = encryption ? 'wss://' : 'ws://';
        this._socket = new WebSocket(scheme + address);

        this._socket.onopen = () => {
            console.log('Connected to WebSocket server');
        };

        this._socket.onclose = () => {
            console.log('Disconnected from WebSocket server');
            // Implement reconnection logic here
        };

        this._socket.onerror = (error) => {
            this._onerror(error, this._handles);
        };

        this._socket.onmessage = (event) => {
            this._onmessage(event.data, this._handles, this._listeners);
        };
    }

    addListener(method, callback) {
        this._listeners[method] = callback;
    }

    removeListener(method) {
        delete this._listeners[method];
    }

    request(method, params, id, successCallback, errorCallback) {
        const requestData = this._formatData(method, params, id);
        this._handles[id] = { success: successCallback, error: errorCallback };
        this._send(requestData);
    }

    batchRequest(requests, successCallback, errorCallback) {
        const batchData = requests.map(({ method, params, id }) => this._formatData(method, params, id));
        this._handles[JSON.stringify(batchData)] = { success: successCallback, error: errorCallback };
        this._send(batchData);
    }

    _formatData(method, params, id) {
        return {
            jsonrpc: '2.0',
            method: this._namespace + method,
            params: params || [],
            id: id
        };
    }

    _send(data) {
        if (this._socket.readyState === WebSocket.OPEN) {
            this._socket.send(JSON.stringify(data));
        } else {
            console.error('WebSocket is not open. Ready state:', this._socket.readyState);
        }
    }

    _onerror(error, handles) {
        Object.values(handles).forEach(handle => {
            if (handle.error) {
                handle.error(error);
            }
        });
        this._handles = {}; // Clear handles on error
    }

    _onmessage(message, handles, listeners) {
        const data = JSON.parse(message);
        if (Array.isArray(data)) {
            data.forEach(item => this._processResponse(item, handles, listeners));
        } else {
            this._processResponse(data, handles, listeners);
        }
    }

    _processResponse(response, handles, listeners) {
        if (response.id !== undefined) {
            const handle = handles[response.id];
            if (handle) {
                if (response.error) {
                    handle.error(response.error);
                } else {
                    handle.success(response.result);
                }
                delete handles[response.id];
            }
        } else if (response.method && listeners[response.method]) {
            listeners[response.method](response.params);
        }
    }
}
