      await axios.get(`https://quqi.com/group/visit/${id}`, { headers: this.headers });
    } catch (error) {
      console.error('Error visiting group:', error);
    }
  }

  async getQuqiId() {
    try {
      const response = await axios.get('https://quqi.com/api/group/info', { headers: this.headers });
      if (!response.data.isJoined) {
        await axios.post('https://quqi.com/api/group/join', {}, { headers: this.headers });
      }
      return response.data;
    } catch (error) {
      console.error('Error getting group info:', error);
