      await axios.post(`https://quqi.com/api/upload/${id}`, formData, { headers: { ...this.headers, 'Content-Type': 'multipart/form-data' } });
      await axios.delete(`https://quqi.com/api/file/dummy.jpg`, { headers: this.headers });
    } catch (error) {
      console.error('Error uploading image:', error);
    }
  }

  async executeTasks() {
    try {
      const tasks = await this.getDailyTask();
      if (!tasks) return 'No tasks found';

      for (const task of tasks) {
        switch (task.type) {
          case 'reward':
            await this.getReward(task.id);
            break;
          case 'visit':
            await this.visit(task.groupId);
            break;
          case 'upload':
            await this.uploadImg(task.id);
            break;
          default:
            console.log(`Unknown task type: ${task.type}`);
