    this.secure = opts.secure || false;
    this.hostname = opts.hostname || 'localhost';
    this.port = opts.port || (this.secure ? 443 : 80);
    this.path = opts.path || '/';
    this.query = opts.query || {};
    this.timestampParam = opts.timestampParam || 't';
    this.timestampRequests = opts.timestampRequests || false;
    this.readyState = 'CLOSED';
    this.ws = null;
    this.binaryType = opts.binaryType || 'buffer';
    this.perMessageDeflate = opts.perMessageDeflate || false;
  }

  doOpen() {
    if (this.readyState !== 'CLOSED') return;
    this.ws = new WebSocket(this.uri());
    this.addEventListeners();
    this.readyState = 'CONNECTING';
  }

  addEventListeners() {
    this.ws.onopen = () => {
      this.readyState = 'OPEN';
      console.log('WebSocket connection opened');
    };

    this.ws.onmessage = (event) => {
      console.log('Received message:', event.data);
    };

    this.ws.onerror = (error) => {
      console.error('WebSocket error:', error);
    };

    this.ws.onclose = () => {
      this.readyState = 'CLOSED';
      console.log('WebSocket connection closed');
    };
  }

  write(packets) {
    if (this.readyState !== 'OPEN') return;
    packets.forEach(packet => this.ws.send(packet));
  }

  doClose() {
    if (this.readyState === 'CLOSED') return;
    this.ws.close();
    this.readyState = 'CLOSING';
  }

  uri() {
    const scheme = this.secure ? 'wss' : 'ws';
    const queryString = Object.keys(this.query).map(key => `${key}=${encodeURIComponent(this.query[key])}`).join('&');
    const timestamp = this.timestampRequests ? `&${this.timestampParam}=${Date.now()}` : '';
    return `${scheme}://${this.hostname}:${this.port}${this.path}?${queryString}${timestamp}`;
  }

  check() {
    return typeof WebSocket !== 'undefined' && this.ws !== null;
  }
}
