class WebSocketStore {
  wsActive = false;
  ws = null;

  constructor(snack, currentUser) {
    this.snack = snack;
    this.currentUser = currentUser;
  }

  listen(callback) {
    if (this.wsActive) return;

    const token = this.currentUser.getToken();
    if (!token) {
      this.snack.show('Authentication required');
      return;
    }

    this.ws = new WebSocket(`wss://example.com/socket?token=${token}`);

    this.ws.onopen = () => {
      this.wsActive = true;
      this.snack.show('WebSocket connected');
    };

    this.ws.onmessage = (event) => {
      const msg = JSON.parse(event.data);
      callback(msg);
    };

    this.ws.onerror = (error) => {
      this.snack.show('WebSocket error', error);
    };

    this.ws.onclose = () => {
      this.wsActive = false;
      this.snack.show('WebSocket disconnected');
      // Attempt to reconnect after a delay
      setTimeout(() => this.listen(callback), 5000);
    };
  }

  close() {
    if (this.ws && this.ws.readyState === WebSocket.OPEN) {
      this.ws.close(1000, 'Normal closure');
    }
    this.ws = null;
    this.wsActive = false;
  }
}
