class WebSocketRPC {
  constructor(_, JSON, name, utils, alerts) {
    this._ = _;
    this.JSON = JSON;
    this.utils = utils;
    this.alerts = alerts;

    this.initialized = false;
    this.handles = [];
    this.sock = null;
    this.conf = null;
    this.scheme = "ws";
    this.onready = null;
  }

  onerror(ev) {
    this._.each(this.handles, function(h) {
      h.error();
    });
    this.handles = [];
    this.initialized = false;
    if (this.onready) {
      this.onready();
      this.onready = null;
    }
  }

  onclose(ev) {
    if (this.handles && this.handles.length)
      this.onerror("Connection reset while calling aria2");
    this.initialized = false;
    if (this.onready) {
      this.onready();
      this.onready = null;
    }
  }

  onopen() {
    console.log("websocket initialized!!!");
    this.initialized = true;
    if (this.onready) {
      this.onready();
      this.onready = null;
    }
  }

  onmessage(message) {
    var data = this.JSON.parse(message.data);

    for (var i = this.handles.length - 1; i >= 0; i--) {
      if (this.handles[i].id === data.id) {
        this.handles[i].success(data);
        this.handles.splice(i, 1);
        return;
      }
    }
  }

  invoke(opts) {
    var data = {
      jsonrpc: 2.0,
      id: this.utils.uuid(),
      method: opts.name,
      params: opts.params && opts.params.length ? opts.params : undefined
    };

    if (data.params && !data.params.length) data.params = undefined;

    this.handles.push({
      success: opts.success || angular.noop,
      error: opts.error || angular.noop,
      id: data.id
    });
    this.sock.send(this.JSON.stringify(data));
  }

  init(conf, onready) {
    this.initialized = false;
    if (this.onready) {
      this.onready();
      this.onready = null;
    }

    if (typeof WebSocket == "undefined") {
      this.alerts.addAlert("Web sockets are not supported! Falling back to JSONP.", "info");
      onready();
      return;
    }
    this.conf = conf || this.conf;
    this.scheme = this.conf.encrypt ? "wss" : "ws";

    if (this.sock) {
      this.sock.onopen = this.sock.onmessage = this.sock.onerror = this.sock.onclose = null;
      this.onerror({ message: "Changing the websocket aria2 server details" });
    }

    try {
      var authUrl =
        this.scheme + "://" + conf.host + ":" + conf.port + (conf.path || "/jsonrpc");
      if (this.conf.auth && this.conf.auth.user && this.conf.auth.pass) {
        authUrl =
          this.scheme +
          "://" +
          this.conf.auth.user +
          ":" +
          this.conf.auth.pass +
          "@" +
          this.conf.host +
          ":" +
          this.conf.port +
          (conf.path || "/jsonrpc");
      }

      this.sock = new WebSocket(authUrl);
      this.sock.onopen = this.onopen.bind(this);
      this.sock.onclose = this.onclose.bind(this);
      this.sock.onerror = this.onerror.bind(this);
      this.sock.onmessage = this.onmessage.bind(this);
      this.onready = onready;
    } catch (ex) {
      console.log("not using websocket for aria2 rpc due to: ", ex);
      this.alerts.addAlert("Web sockets not working due to " + ex.message, "info");
      onready();
    }
  }
}

