class WebSocketRPC {
  constructor(_, JSON, utils, alerts) {
    this._ = _;
    this.JSON = JSON;
    this.utils = utils;
    this.alerts = alerts;
    this.initialized = false;
    this.handles = [];
    this.sock = null;
    this.conf = null;
    this.scheme = 'ws';
    this.onready = null;
  }

  onerror(ev) {
    this.alerts.error('WebSocket error:', ev);
    this.handles.forEach(handle => handle.error(new Error('Connection lost')));
    this.handles = [];
    this.initialized = false;
  }

  onclose(ev) {
    this.alerts.warn('WebSocket closed:', ev);
    this.handles.forEach(handle => handle.error(new Error('Connection closed')));
    this.handles = [];
    this.initialized = false;
  }

  onopen() {
    this.initialized = true;
    if (this.onready) {
      this.onready();
    }
  }

  onmessage(message) {
    const data = this.JSON.parse(message.data);
    if (data.id) {
      const handle = this._.find(this.handles, { id: data.id });
      if (handle) {
        if (data.error) {
          handle.error(data.error);
        } else {
          handle.success(data.result);
        }
        this.handles = this._.without(this.handles, handle);
      }
    }
  }

  invoke(opts) {
    if (!this.initialized) {
      throw new Error('WebSocket not initialized');
    }
    const id = this.utils.uuid();
    const request = {
      jsonrpc: '2.0',
      method: opts.name,
      params: opts.params,
      id: id
    };
    this.handles.push({
      id: id,
      success: opts.success,
      error: opts.error
    });
    this.sock.send(this.JSON.stringify(request));
  }

  init(conf, onready) {
    this.conf = conf;
    this.scheme = conf.encrypt ? 'wss' : 'ws';
    const url = `${this.scheme}://${conf.host}:${conf.port}${conf.path || ''}`;
    this.sock = new WebSocket(url);
    this.sock.onerror = this.onerror.bind(this);
    this.sock.onclose = this.onclose.bind(this);
    this.sock.onopen = this.onopen.bind(this);
    this.sock.onmessage = this.onmessage.bind(this);
    this.onready = onready;
  }
}
