class MonitorManagerProxyClientImpl {
  constructor() {
    this.webSocket = undefined;
    this.wsPort = undefined;
  }

  async connect(addressPort) {
    return new Promise((resolve, reject) => {
      this.webSocket = new WebSocket(`ws://localhost:${addressPort}`);
      this.wsPort = addressPort;

      this.webSocket.onopen = () => resolve();
      this.webSocket.onerror = (error) => reject(error);
      this.webSocket.onmessage = (event) => this.handleMessage(event.data);
      this.webSocket.onclose = () => this.handleClose();
    });
  }

  disconnect() {
    if (this.webSocket) {
      this.webSocket.close();
      this.webSocket = undefined;
    }
  }

  send(message) {
    if (this.webSocket && this.webSocket.readyState === WebSocket.OPEN) {
      this.webSocket.send(JSON.stringify({ command: 'send', data: message }));
    }
  }

  changeSettings(settings) {
    if (this.webSocket && this.webSocket.readyState === WebSocket.OPEN) {
      this.webSocket.send(JSON.stringify({ command: 'settings', data: settings }));
    }
  }

  async isWSConnected() {
    return this.webSocket !== undefined && this.webSocket.readyState === WebSocket.OPEN;
  }

  getWebSocketPort() {
    return this.wsPort;
  }

  handleMessage(data) {
    const message = JSON.parse(data);
    if (message.command === 'settings') {
      this.emit('settingsChange', message.data);
    } else if (message.command === 'message') {
      this.emit('message', message.data);
    }
  }

  handleClose() {
    this.emit('disconnect');
  }

  emit(eventName, data) {
    const listeners = this.listeners[eventName] || [];
    listeners.forEach(listener => listener(data));
  }

  on(eventName, listener) {
    if (!this.listeners) this.listeners = {};
    if (!this.listeners[eventName]) this.listeners[eventName] = [];
    this.listeners[eventName].push(listener);
  }
}
