    socket.onopen = () => {
        console.log('WebSocket connection established');
        requestMicrophonePermission();
    };

    socket.onmessage = (event) => {
        const response = JSON.parse(event.data);
        if (response.type === 'status') {
            isServerReady = response.ready;
            if (isServerReady && isCapturing) {
                startAudioCapture();
            }
        } else if (response.type === 'transcription') {
            handleTranscription(response.text);
        }
    };

    socket.onerror = (error) => {
        console.error('WebSocket error:', error);
        browser.runtime.sendMessage({ type: 'error', message: 'WebSocket error' });
    };

    socket.onclose = () => {
        console.log('WebSocket connection closed');
        browser.runtime.sendMessage({ type: 'disconnected' });
    };

    function requestMicrophonePermission() {
        navigator.mediaDevices.getUserMedia({ audio: true })
            .then(stream => {
                mediaRecorder = new MediaRecorder(stream);
                mediaRecorder.ondataavailable = handleAudioData;
                isCapturing = true;
                if (isServerReady) {
                    startAudioCapture();
                }
            })
            .catch(error => {
                console.error('Error accessing microphone:', error);
                browser.runtime.sendMessage({ type: 'error', message: 'Microphone access denied' });
            });
    }

    function startAudioCapture() {
        if (mediaRecorder.state === 'inactive') {
            mediaRecorder.start();
        }
