class BingChatWebSocket {
  constructor() {
    this.webSockets = {};
  }

  async getSocketId() {
    return new Promise((resolve, reject) => {
      const socketId = Date.now().toString();
      const ws = new WebSocket('wss://chat.example.com');

      ws.onopen = () => {
        ws.send(JSON.stringify({ type: 1, content: 'handshake' }));
      };

      ws.onmessage = (event) => {
        const data = JSON.parse(event.data);
        if (data.type === 0 && data.content === 'success') {
          this.webSockets[socketId] = ws;
          resolve(socketId);
        } else {
          reject(new Error('Handshake failed'));
        }
      };

      ws.onerror = (error) => {
        reject(error);
      };

      ws.onclose = () => {
        delete this.webSockets[socketId];
      };
    });
  }

  async ping(socketId) {
    return new Promise((resolve, reject) => {
      const ws = this.webSockets[socketId];
      if (!ws || ws.readyState !== WebSocket.OPEN) {
        reject(new Error('WebSocket is not open'));
        return;
      }

      ws.send(JSON.stringify({ type: 3, content: 'ping' }));

      ws.onmessage = (event) => {
        const data = JSON.parse(event.data);
        if (data.type === 4 && data.content === 'pong') {
          resolve();
        }
      };

      ws.onerror = (error) => {
        reject(error);
      };
    });
  }

  async send(socketId, msg, onMessage) {
    return new Promise((resolve, reject) => {
      const ws = this.webSockets[socketId];
      if (!ws || ws.readyState !== WebSocket.OPEN) {
        reject(new Error('WebSocket is not open'));
        return;
      }

      ws.send(JSON.stringify(msg));

      ws.onmessage = (event) => {
        const data = JSON.parse(event.data);
        onMessage(data);
        if (data.type === 2) {
          resolve(data);
        }
      };

      ws.onerror = (error) => {
        reject(error);
      };
    });
  }

  async close(socketId) {
    return new Promise((resolve) => {
      const ws = this.webSockets[socketId];
      if (ws) {
        ws.close();
      }
      delete this.webSockets[socketId];
      resolve();
    });
  }
