class WebSocketClient {
  constructor() {
    this.wsClient = null;
    this.sendQueue = [];
    this.pendingReconnect = null;
  }

  fireSendQueue() {
    while (this.sendQueue.length > 0) {
      const { resolve, reject, message } = this.sendQueue.shift();
      try {
        this.wsClient.send(message);
        resolve();
      } catch (error) {
        reject(error);
      }
    }
  }

  clearSendQueue() {
    this.sendQueue.forEach(({ reject }) => reject(new Error('Connection closed')));
    this.sendQueue = [];
  }

  planToReconnect(rpcUrl, options) {
    clearTimeout(this.pendingReconnect);
    this.pendingReconnect = setTimeout(() => this.reconnect(rpcUrl, options), 5000);
  }

  init() {
    if (this.wsClient) {
      this.wsClient.close();
      this.clearSendQueue();
    }
  }

  connect(rpcUrl, options, onOpenCallback, onCloseCallback, onMessageCallback) {
    this.init();
    this.wsClient = new WebSocket(rpcUrl, options);

    this.wsClient.onopen = () => {
      onOpenCallback();
      this.fireSendQueue();
    };

    this.wsClient.onclose = (event) => {
      onCloseCallback(event);
      this.planToReconnect(rpcUrl, options);
    };

    this.wsClient.onmessage = (event) => {
      onMessageCallback(event.data);
    };

    this.wsClient.onerror = (error) => {
      console.error('WebSocket error:', error);
    };
  }

  reconnect(rpcUrl, options) {
    this.connect(rpcUrl, options, this.onOpenCallback, this.onCloseCallback, this.onMessageCallback);
  }

  send(requestContext) {
    return new Promise((resolve, reject) => {
      this.sendQueue.push({ resolve, reject, message: requestContext });
      if (this.wsClient && this.wsClient.readyState === WebSocket.OPEN) {
        this.fireSendQueue();
      }
    });
  }

  getReadyState() {
    return this.wsClient ? this.wsClient.readyState : null;
  }
}
