      to,
      subject,
      text,
      ...options.payload
    };

    axios.post(transporter.url, payload, {
      headers: {
        'Authorization': `Bearer ${transporter.apiKey}`,
        'Content-Type': 'application/json'
      }
    })
    .then(response => {
      resolve(response.data);
    })
