const axios = require('axios');
const fs = require('fs');
const path = require('path');

async function downloadIcons(url, style, callback) {
    const targetImagePath = path.join(__dirname, 'icons');
    const logPath = path.join(targetImagePath, 'log.txt');
    const breakOnError = true;

    try {
        // Ensure the target image path exists
        if (!fs.existsSync(targetImagePath)) {
            fs.mkdirSync(targetImagePath);
        }

        // Fetch icon data from the URL
        const response = await axios.get(url, {
            headers: {
                Accept: 'application/json',
                'Access-Control-Allow-Origin': '*',
                'Access-Control-Allow-Methods': 'GET',
                'Access-Control-Allow-Headers': 'Content-Type',
                withCredentials: true,
            },
        });

        const iconsData = response.data.icons;
        const processedIcons = [];
        const seenNames = new Set();

        for (const icon of iconsData) {
            if (icon.style !== style) continue;

            const iconName = icon.name;
            const iconCode = icon.code;
            const iconUnicode = icon.unicode;
            const iconPro = icon.pro;
            const iconCategory = icon.category;
            const iconTags = icon.tags;
            const iconSvg = icon.svg;

            if (seenNames.has(iconName)) {
                continue;
            }

            seenNames.add(iconName);

            const iconFileName = `${iconName}.svg`;
            const iconFilePath = path.join(targetImagePath, iconFileName);

            try {
                fs.writeFileSync(iconFilePath, iconSvg);
            } catch (writeError) {
                console.error(`Failed to write ${iconFileName}:`, writeError);
                if (breakOnError) {
                    return callback(writeError, null);
                }
            }

            const iconInfo = {
                name: iconName,
                svg: iconSvg,
                category: iconCategory,
                style: icon.style,
                tags: iconTags,
                code: iconCode,
                unicode: iconUnicode,
                pro: iconPro,
            };

            processedIcons.push(iconInfo);
        }

        // Sort icons by name before returning
        processedIcons.sort((a, b) => a.name.localeCompare(b.name));

        // Log the processed icons
        fs.appendFileSync(logPath, JSON.stringify(processedIcons, null, 2));

        callback(null, processedIcons);
    } catch (error) {
        console.error('Error downloading icons:', error);
        callback(error, null);
    }
}
