class EventSource {
  static CONNECTING = 0;
  static OPEN = 1;
  static CLOSED = 2;

  constructor(url) {
    this.URL = url;
    this.readyState = EventSource.CONNECTING;
    this._pollTimer = null;
    this._xhr = null;
    this.listeners = {};
    this.poll();
  }

  poll() {
    if (this.readyState !== EventSource.CONNECTING && this.readyState !== EventSource.OPEN) return;

    this._xhr = new XMLHttpRequest();
    this._xhr.open('GET', this.URL, true);
    this._xhr.setRequestHeader('Accept', 'text/event-stream');
    this._xhr.setRequestHeader('Cache-Control', 'no-cache');
    this._xhr.setRequestHeader('Connection', 'keep-alive');

    this._xhr.onreadystatechange = () => {
      if (this._xhr.readyState === XMLHttpRequest.DONE) {
        if (this.readyState === EventSource.CONNECTING) {
          this.readyState = EventSource.OPEN;
          this.dispatchEvent('open', {});
        }
        this.poll();
      } else if (this._xhr.readyState === XMLHttpRequest.OPENED) {
        this.dispatchEvent('readystatechange', { readyState: this._xhr.readyState });
      }
    };

    this._xhr.onprogress = (event) => {
      if (this.readyState !== EventSource.OPEN) return;

      const responseText = this._xhr.responseText;
      const lines = responseText.split('\n').filter(line => line.trim().length > 0);

      let data = '';
      let eventType = 'message';
      let lastEventId = '';

      for (const line of lines) {
        const parts = line.split(': ');
        const field = parts[0];
        const value = parts.slice(1).join(': ');

        switch (field) {
          case 'data':
            data += value + '\n';
            break;
          case 'event':
            eventType = value;
            break;
          case 'id':
            lastEventId = value;
            break;
        }
      }

      if (data.length > 0) {
        const messageEvent = new MessageEvent(data.trim(), this.URL, lastEventId);
        this.dispatchEvent(eventType, messageEvent);
      }
    };

    this._xhr.onerror = () => {
      if (this.readyState === EventSource.OPEN) {
        this.readyState = EventSource.CONNECTING;
        this.dispatchEvent('error', { type: 'error' });
        this._pollTimer = setTimeout(() => this.poll(), 3000);
      }
    };

    this._xhr.send();
  }

  close() {
    if (this.readyState !== EventSource.CLOSED) {
      this.readyState = EventSource.CLOSED;
      if (this._xhr) {
        this._xhr.abort();
        this._xhr = null;
      }
      clearTimeout(this._pollTimer);
      this.dispatchEvent('close', {});
    }
  }

  dispatchEvent(type, event) {
    if (this.listeners[type]) {
      this.listeners[type].forEach(handler => handler.call(this, event));
    }
  }

  addEventListener(type, handler) {
    if (!this.listeners[type]) {
      this.listeners[type] = [];
    }
    this.listeners[type].push(handler);
  }

  removeEventListener() {
    // Placeholder for removing event listeners
  }
}

class MessageEvent {
  constructor(data, origin, lastEventId) {
    this.data = data;
    this.type = 'message';
    this.lastEventId = lastEventId;
    this.origin = origin;
  }
}
