import { RequestMethod } from 'umi-request';
import { checkPermissions } from './permissions';

interface TRequestOption {
  method: string;
  headers?: object;
  data?: any;
  requestType?: string;
}

class BasicRequestService implements IRequestService {
  private requestMethod: RequestMethod;

  constructor() {
    this.requestMethod = new RequestMethod();
  }

  async download(url: string): Promise<Blob> {
    if (!checkPermissions('download')) {
      throw new Error('Permission denied for downloading files.');
    }
    return new Promise((resolve, reject) => {
      const xhr = new XMLHttpRequest();
      xhr.open('GET', url, true);
      xhr.responseType = 'blob';
      xhr.onload = () => {
        if (xhr.status === 200) {
          resolve(xhr.response);
        } else {
          reject(new Error(`Failed to download file. Status: ${xhr.status}`));
        }
      };
      xhr.onerror = () => {
        reject(new Error('Network error occurred while downloading the file.'));
      };
      xhr.send();
    });
  }

  async request(url: string, options: TRequestOption): Promise<any> {
    const { method, headers, data, requestType } = options;
    switch (method.toLowerCase()) {
      case 'get':
        return this.requestMethod.get(url, { headers });
      case 'put':
        return this.requestMethod.put(url, { headers, data });
      case 'post':
        return this.requestMethod.post(url, { headers, data, requestType });
      default:
        throw new Error(`Unsupported request method: ${method}`);
    }
  }
}
