class AjaxTransport {
  constructor() {
    this.xhrId = 0;
    this.xhrCallbacks = {};
    this.xhrSuccessStatus = {
      200: true,
      201: true,
      204: true,
      304: true
    };
    this.xhrSupported = typeof XMLHttpRequest !== 'undefined' ? new XMLHttpRequest() : false;
  }

  createXHR() {
    if (!this.xhrSupported) return undefined;
    return new XMLHttpRequest();
  }

  sendRequest(options, headers, complete) {
    const xhr = this.createXHR();
    if (!xhr) return;

    const { type, url, async = true, data } = options;
    xhr.open(type, url, async);

    for (const header in headers) {
      xhr.setRequestHeader(header, headers[header]);
    }

    xhr.onreadystatechange = () => {
      if (xhr.readyState === 4) {
        if (this.xhrSuccessStatus[xhr.status]) {
          complete(null, xhr.responseText);
        } else {
          complete(new Error(`Request failed with status ${xhr.status}`));
        }
        delete this.xhrCallbacks[xhrId];
      }
    };

    xhrId++;
    this.xhrCallbacks[this.xhrId] = complete;
    xhr.send(data);
  }

  abortRequest(callback) {
    for (const id in this.xhrCallbacks) {
      const xhr = this.createXHR();
      if (xhr) {
        xhr.abort();
        callback();
        delete this.xhrCallbacks[id];
        break;
      }
    }
  }

  getTransport(options) {
    if (this.xhrSupported) {
      return {
        send: (headers, complete) => this.sendRequest(options, headers, complete),
        abort: callback => this.abortRequest(callback)
      };
    }
    return undefined;
  }
}
