const express = require('express');
const rest = require('connect-rest');
const bodyParser = require('body-parser');
const mongoose = require('mongoose');
const http = require('http');
const domain = require('domain');
const cluster = require('cluster');
const os = require('os');

// Assuming Attraction is a Mongoose model defined elsewhere
const Attraction = mongoose.model('Attraction', new mongoose.Schema({
  name: String,
  description: String,
  location: String,
  email: String,
  approved: Boolean
}));

function startServer() {
  const app = express();
  const server = http.createServer(app);

  // Middleware
  app.use(bodyParser.json());
  app.use(rest.rester());

  // REST API Routes
  app.get('/attractions', rest.get(function(req, content, cb) {
    Attraction.find({ approved: true }, (err, attractions) => {
      if (err) return cb(err);
      cb(null, attractions);
    });
  }));

  app.post('/attraction', rest.post(function(req, content, cb) {
    const { name, description, location, email } = req.body;
    if (!name || !description || !location || !email) {
      return cb(new Error('Missing required fields'));
    }
    const attraction = new Attraction({ name, description, location, email, approved: false });
    attraction.save((err, savedAttraction) => {
      if (err) return cb(err);
      cb(null, { id: savedAttraction._id });
    });
  }));

  app.get('/attraction/:id', rest.get(function(req, content, cb) {
    Attraction.findById(req.params.id, (err, attraction) => {
      if (err) return cb(err);
      if (!attraction) return cb(new Error('Attraction not found'));
      cb(null, attraction);
    });
  }));

  // Error Handling
  const errorHandlerDomain = domain.create();
  errorHandlerDomain.on('error', (err) => {
    console.error('Uncaught exception in domain:', err.stack);
    server.close(() => {
      process.exit(1);
    });
  });

  server.on('request', (req, res) => {
    errorHandlerDomain.run(() => {
      app(req, res);
    });
  });

  // Start server
  server.listen(process.env.PORT || 3000, () => {
    console.log(`Server running on port ${server.address().port}`);
  });

  return server;
}

if (cluster.isMaster) {
  const numCPUs = os.cpus().length;
  for (let i = 0; i < numCPUs; i++) {
    cluster.fork();
  }

  cluster.on('exit', (worker, code, signal) => {
    console.log(`Worker ${worker.process.pid} died`);
    cluster.fork();
  });
} else {
  startServer();
}
