const express = require('express');
const bodyParser = require('body-parser');
const cors = require('cors');
const mongoose = require('mongoose');
const { Prediction, PageView } = require('./src/models');
const { shouldPrefetch, getPreviousPageId } = require('./utils/helpers');

const app = express();
const PORT = process.env.PORT || 3000;

// Middleware setup
app.use(cors());
app.use(bodyParser.json());

// Database connection
mongoose.connect(require('./config').mongoURI, { useNewUrlParser: true, useUnifiedTopology: true })
  .then(() => console.log('MongoDB connected'))
  .catch(err => console.error('MongoDB connection error:', err));

// Route to track page views
app.post('/track-view', async (req, res) => {
  const { pageId, userId } = req.body;
  try {
    await PageView.create({ pageId, userId });
    res.status(201).send('Page view recorded');
  } catch (err) {
    res.status(500).send('Error recording page view');
  }
});

// Route to get predictions
app.get('/predictions/:userId', async (req, res) => {
  const { userId } = req.params;
  try {
    const predictions = await Prediction.find({ userId });
    res.json(predictions);
  } catch (err) {
    res.status(500).send('Error fetching predictions');
  }
});

// Route to determine prefetch paths
app.get('/prefetch/:userId', async (req, res) => {
  const { userId } = req.params;
  try {
    const previousPageId = await getPreviousPageId(userId);
    if (previousPageId && shouldPrefetch(previousPageId)) {
      const prefetchPaths = await Prediction.find({ userId, previousPageId });
      res.json(prefetchPaths);
    } else {
      res.json([]);
    }
  } catch (err) {
    res.status(500).send('Error determining prefetch paths');
  }
});

// Start the server
app.listen(PORT, () => console.log(`Server running on port ${PORT}`));

module.exports = app;
