const express = require('express');
const cors = require('cors');
const helmet = require('helmet');
const compression = require('compression');
const http = require('http');
const https = require('https');
const fs = require('fs');
const path = require('path');

function createExpressApp() {
  const app = express();

  // Middleware setup
  app.use(cors());
  app.use(helmet());
  app.use(compression());

  // Security headers
  app.use((req, res, next) => {
    res.setHeader('X-Content-Type-Options', 'nosniff');
    res.setHeader('X-Frame-Options', 'SAMEORIGIN');
    res.setHeader('X-XSS-Protection', '1; mode=block');
    next();
  });

  // Static files
  app.use(express.static(path.join(__dirname, 'public')));

  // API routes
  app.get('/api/status', (req, res) => {
    res.json({ status: 'ok' });
  });

  // Error handler
  app.use((err, req, res, next) => {
    console.error(err.stack);
    res.status(500).send('Something broke!');
  });

  // Start servers
  const httpPort = CONFIG.httpPort || process.env.HTTP_PORT || 3000;
  const httpsPort = CONFIG.httpsPort || process.env.HTTPS_PORT;

  const server = http.createServer(app);
  server.listen(httpPort, () => {
    console.log(`HTTP Server running on port ${httpPort}`);
  });

  if (httpsPort) {
    const privateKey = fs.readFileSync(CONFIG.sslKeyPath || process.env.SSL_KEY_PATH, 'utf8');
    const certificate = fs.readFileSync(CONFIG.sslCertPath || process.env.SSL_CERT_PATH, 'utf8');
    const credentials = { key: privateKey, cert: certificate };

    const httpsServer = https.createServer(credentials, app);
    httpsServer.listen(httpsPort, () => {
      console.log(`HTTPS Server running on port ${httpsPort}`);
    });
  }

  return app;
}

module.exports = createExpressApp;
