    const errorPromise = new Promise(resolve => ws.once('error', () => resolve()));
    const closePromise = new Promise(resolve => ws.once('close', () => resolve()));
    let state = await Promise.race([openPromise, errorPromise, closePromise, this._disconnectPromise]);
