    this.url = url;
    this.name = data.name || '-1';
    this.installDirectory = data.installDirectory || require('path').join(__dirname, 'bin');
    this.binaryDirectory = '-1';
    this.binaryPath = '-1';
  }

  async install() {
    return new Promise(async (resolve, reject) => {
      try {
        const installDir = this._getInstallDirectory();
        await require('fs').promises.mkdir(installDir, { recursive: true });
        const response = await fetch(this.url);
        if (!response.ok) throw new Error(`Download failed: ${response.statusText}`);
        const buffer = await response.arrayBuffer();
        const tempFilePath = require('path').join(require('os').tmpdir(), `${this.name}.tmp`);
        await require('fs').promises.writeFile(tempFilePath, Buffer.from(buffer));
        await require('child_process').execSync(`tar -xzf ${tempFilePath} -C ${installDir}`);
        require('fs').promises.unlink(tempFilePath);
        this.binaryDirectory = installDir;
        this.binaryPath = require('path').join(installDir, this.name);
        resolve();
      } catch (err) {
        reject(err);
      }
    });
  }

  _getInstallDirectory() {
    return require('path').join(this.installDirectory, this.name);
  }
}

