const axios = require('axios');
const CustomError = require('../CustomError');

class BuildService {
  constructor() {
    this.operations = [
      {
        type: 'PACKAGE_BUILD_STATS',
        endpoint: '/size',
        methodName: 'getPackageStats',
      },
      {
        type: 'PACKAGE_EXPORTS',
        endpoint: '/exports',
        methodName: 'getAllPackageExports',
      },
      {
        type: 'PACKAGE_EXPORTS_SIZES',
        endpoint: '/exports-sizes',
        methodName: 'getPackageExportSizes',
      },
    ];
  }

  async _makeHttpRequest(operation, packageString) {
    try {
      const response = await axios.get(
        `${process.env.BUILD_SERVICE_ENDPOINT}${operation.endpoint}?p=${encodeURIComponent(packageString)}`
      );
      return response.data;
    } catch (error) {
      this._handleError(error, operation.type);
    }
  }

  _handleError(error, operationType) {
    if (error.response) {
      const contents = error.response.data;
      throw new CustomError(
        contents.name || 'BuildError',
        contents.originalError,
        contents.extra
      );
    } else if (error.request) {
      throw new CustomError('BuildError', {
        operation: operationType,
        reason: 'BUILD_SERVICE_UNREACHABLE',
        url: error.request._currentUrl,
      });
    } else {
      throw new CustomError('BuildError', error.message, {
        operation: operationType,
      });
    }
  }

  async getPackageBuildStats(packageString) {
    const operation = this.operations.find(op => op.type === 'PACKAGE_BUILD_STATS');
    return await this._makeHttpRequest(operation, packageString);
  }

  async getPackageExports(packageString) {
    const operation = this.operations.find(op => op.type === 'PACKAGE_EXPORTS');
    return await this._makeHttpRequest(operation, packageString);
  }

  async getPackageExportSizes(packageString) {
    const operation = this.operations.find(op => op.type === 'PACKAGE_EXPORTS_SIZES');
    return await this._makeHttpRequest(operation, packageString);
  }
}

