const express = require('express');
const fs = require('fs');
const path = require('path');
const responseTime = require('response-time');

function setupExpressServer() {
  const app = express();
  const imagePath = path.join(__dirname, 'CapitalOne_Digital_Skills_Infographic_BW.jpg');

  app.use(responseTime());

  app.get('/stream', (req, res) => {
    const stream = fs.createReadStream(imagePath);
    stream.pipe(res);
  });

  app.get('/non-stream', (req, res) => {
    fs.readFile(imagePath, (err, data) => {
      if (err) {
        res.status(500).send(err.message);
        return;
      }
      res.send(data);
    });
  });

  app.get('/stream-range', (req, res) => {
    const range = req.headers.range;
    if (!range) {
      return res.status(416).send('Range not satisfied');
    }

    const { size } = fs.statSync(imagePath);
    const [start, end] = range.replace(/bytes=/, '').split('-').map(Number);
    const chunkSize = end - start + 1;
    const fileStream = fs.createReadStream(imagePath, { start, end });

    res.writeHead(206, {
      'Content-Range': `bytes ${start}-${end}/${size}`,
      'Accept-Ranges': 'bytes',
      'Content-Length': chunkSize,
      'Content-Type': 'image/jpeg'
    });

    fileStream.pipe(res);
  });

  app.get('/non-stream-range', (req, res) => {
    const size = fs.statSync(imagePath).size;
    const range = req.headers.range;
    if (!range) {
      return res.status(416).send('Range not satisfied');
    }

    const [start, end] = range.replace(/bytes=/, '').split('-').map(Number);
    const chunkSize = end - start + 1;
    const data = fs.readFileSync(imagePath);
    const chunk = data.slice(start, end + 1);

    res.writeHead(206, {
      'Content-Range': `bytes ${start}-${end}/${size}`,
      'Accept-Ranges': 'bytes',
      'Content-Length': chunkSize,
      'Content-Type': 'image/jpeg'
    });

    res.end(chunk);
  });

  app.listen(3000, () => {
    console.log('Server is running on http://localhost:3000');
  });
}

setupExpressServer();

