  res.sendFile(__dirname + '/index.html');
});

// Route to extract specific elements' innerText and href attributes from a URL
app.post('/query', async (req, res) => {
  const { url, selectors } = req.body;
  try {
    const client = await Cdp({ target: { url } });
    const { DOM, Page } = client;

    await Page.enable();
    await Page.navigate({ url });
    await Page.loadEventFired();

    await DOM.enable();
    const result = {};
    for (const selector of selectors) {
      const element = await DOM.querySelector({ nodeId: 0, selector });
      if (element && element.nodeId) {
        const content = await DOM.getOuterHTML({ nodeId: element.nodeId });
        const href = await DOM.getAttributeValue({ nodeId: element.nodeId, name: 'href' });
        result[selector] = {
          innerText: content.outerHTML,
          href: href.value || null
        };
      }
    }

    await client.close();
    res.json(result);
  } catch (error) {
    console.error('Error querying webpage:', error);
    res.status(500).json({ error: 'Failed to query webpage elements' });
  }
});

// Route to retrieve the complete HTML of a given URL
app.post('/fullHtml', async (req, res) => {
  const { url } = req.body;
