    await Page.enable();
    await Page.navigate({ url });
    await Page.loadEventFired();

    const content = await Page.getContent();
    await client.close();
    res.send(content);
  } catch (error) {
    console.error('Error retrieving full HTML:', error);
    res.status(500).json({ error: 'Failed to retrieve full HTML content' });
  }
});

