      `;
      res.writeHead(200, { 'Content-Type': 'text/html' });
      res.end(html);
      return;
    }

    const uniqueId = uuidv4();
    const filePath = path.join(__dirname, 'requests', `${uniqueId}.txt`);
    const fileStream = fs.createWriteStream(filePath);

    fileStream.on('open', () => {
      req.pipe(fileStream);
      console.log(`Connection established. File created: ${filePath}`);
      connectionCounter++;
      activeStreams[uniqueId] = fileStream;
    });

    fileStream.on('close', () => {
      console.log(`Connection closed. File saved: ${filePath}`);
      delete activeStreams[uniqueId];
      connectionCounter--;
    });

    req.on('end', () => {
      res.writeHead(200, { 'Content-Type': 'text/plain' });
      res.end(`Received request. Connection count: ${connectionCounter}\n`);
    });
  });

  serverInstance.on('connection', (socket) => {
    console.log('New connection established.');
  });

  serverInstance.on('close', () => {
