class Server {
  constructor(config) {
    this.config = config;
    this._http = null;
    this.sockets = [];
    this.path = config.path || '.';
  }

  updateServer() {
    this._http = http.createServer((req, res) => {
      const filePath = path.join(this.path, req.url);
      const file = send(req, filePath, { root: this.path });

      file.on('error', (err) => {
        if (err.status === 404) {
          const dirPath = path.dirname(filePath);
          fs.readdir(dirPath, (err, files) => {
            if (err) {
              res.writeHead(500, { 'Content-Type': 'text/plain' });
              return res.end('Internal Server Error');
            }
            let html = '<!DOCTYPE html><html><head><title>Directory Listing</title></head><body><h1>Directory Listing</h1><ul>';
            files.forEach(file => {
              html += `<li><a href="${encodeURIComponent(file)}">${file}</a></li>`;
            });
            html += '</ul></body></html>';
            res.writeHead(200, { 'Content-Type': 'text/html' });
            res.end(html);
          });
        } else {
          res.writeHead(err.status || 500, { 'Content-Type': 'text/plain' });
          res.end(err.message);
        }
      });

      file.on('directory', (dir) => {
        const dirPath = path.join(this.path, dir);
        fs.readdir(dirPath, (err, files) => {
          if (err) {
            res.writeHead(500, { 'Content-Type': 'text/plain' });
            return res.end('Internal Server Error');
          }
          let html = '<!DOCTYPE html><html><head><title>Directory Listing</title></head><body><h1>Directory Listing</h1><ul>';
          files.forEach(file => {
            html += `<li><a href="${encodeURIComponent(file)}">${file}</a></li>`;
          });
          html += '</ul></body></html>';
          res.writeHead(200, { 'Content-Type': 'text/html' });
          res.end(html);
        });
      });

      file.pipe(res);
    });

    this._http.on('connection', (socket) => {
      this.sockets.push(socket);
      socket.on('close', () => {
        const index = this.sockets.indexOf(socket);
        if (index !== -1) {
          this.sockets.splice(index, 1);
        }
      });
    });
  }

  start(callback) {
    this.updateServer();
    this._http.listen(this.config.port, () => {
      console.log(`Server running on port ${this.config.port}`);
      callback();
    });
  }

  stop(callback) {
    this._http.close(() => {
      this.sockets.forEach(socket => socket.destroy());
      this.sockets = [];
      console.log('Server stopped');
      callback();
    });
  }
}

