const http = require('http');
const fs = require('fs');
const path = require('path');
const url = require('url');

function createServer(port = 90) {
  const staticDirs = ['../src', '../dist'];
  const cachedFiles = {};
  const loadedFiles = [];

  function loadStaticFiles() {
    staticDirs.forEach(dir => {
      fs.readdirSync(dir).forEach(file => {
        const filePath = path.join(dir, file);
        if (fs.statSync(filePath).isFile()) {
          try {
            const content = fs.readFileSync(filePath, 'utf8');
            cachedFiles[path.join(dir, file)] = { content, type: 'text' };
            loadedFiles.push(filePath);
          } catch (err) {
            console.error(`Error reading file ${filePath}:`, err.message);
          }
        }
      });
    });
  }

  function getContentType(filePath) {
    const ext = path.extname(filePath).toLowerCase();
    switch (ext) {
      case '.js': return 'application/javascript';
      case '.css': return 'text/css';
      case '.html': return 'text/html';
      case '.json': return 'application/json';
      case '.png': return 'image/png';
      case '.jpg':
      case '.jpeg': return 'image/jpeg';
      case '.gif': return 'image/gif';
      case '.ico': return 'image/x-icon';
      case '.svg': return 'image/svg+xml';
      case '.mp3': return 'audio/mpeg';
      case '.wav': return 'audio/wav';
      case '.ogg': return 'audio/ogg';
      case '.webm': return 'video/webm';
      case '.mp4': return 'video/mp4';
      default: return 'application/octet-stream';
    }
  }

  function handleRequest(req, res) {
    const parsedUrl = url.parse(req.url, true);
    const pathname = parsedUrl.pathname;

    if (pathname === '/favicon.ico') {
      return serveFile(path.join(staticDirs[0], 'favicon.ico'), res);
    }

    if (pathname.startsWith('/core/')) {
      return handleCoreJSConcatenation(req, res, pathname);
    }

    if (pathname.startsWith('/plugins/')) {
      return handlePluginJSConcatenation(req, res, pathname);
    }

    if (pathname === '/') {
      return serveFile(path.join(staticDirs[0], 'index.html'), res);
    }

    if (pathname.endsWith('.js') || pathname.endsWith('.css') || pathname.endsWith('.html') || pathname.endsWith('.json')) {
      return serveTextFile(pathname, res);
    }

    if (pathname.endsWith('.png') || pathname.endsWith('.jpg') || pathname.endsWith('.jpeg') || pathname.endsWith('.gif') ||
        pathname.endsWith('.ico') || pathname.endsWith('.svg') || pathname.endsWith('.mp3') || pathname.endsWith('.wav') ||
        pathname.endsWith('.ogg') || pathname.endsWith('.webm') || pathname.endsWith('.mp4')) {
      return serveBinaryFile(pathname, res);
    }

    res.writeHead(404, { 'Content-Type': 'text/plain' });
    res.end('404 Not Found');
  }

  function serveTextFile(filePath, res) {
    const fileKey = filePath;
    if (cachedFiles[fileKey]) {
      res.writeHead(200, { 'Content-Type': getContentType(fileKey) });
      res.end(cachedFiles[fileKey].content);
    } else {
      res.writeHead(404, { 'Content-Type': 'text/plain' });
      res.end('404 Not Found');
    }
  }

  function serveBinaryFile(filePath, res) {
    const fileKey = filePath;
    if (cachedFiles[fileKey]) {
      res.writeHead(200, { 'Content-Type': getContentType(fileKey) });
      res.end(Buffer.from(cachedFiles[fileKey].content));
    } else {
      res.writeHead(404, { 'Content-Type': 'text/plain' });
      res.end('404 Not Found');
    }
  }

  function serveFile(filePath, res) {
    if (fs.existsSync(filePath)) {
      const stats = fs.statSync(filePath);
      const contentType = getContentType(filePath);
      res.writeHead(200, { 'Content-Type': contentType });
      const readStream = fs.createReadStream(filePath);
      readStream.pipe(res);
    } else {
      res.writeHead(404, { 'Content-Type': 'text/plain' });
      res.end('404 Not Found');
    }
  }

  function handleCoreJSConcatenation(req, res, pathname) {
    const files = ['core1.js', 'core2.js', 'core3.js'];
    let content = '';
    files.forEach(file => {
      const filePath = path.join(staticDirs[0], 'core', file);
      if (fs.existsSync(filePath)) {
        const fileContent = fs.readFileSync(filePath, 'utf8');
        content += fileContent + '\n';
      }
    });
    res.writeHead(200, { 'Content-Type': 'application/javascript' });
    res.end(content);
  }

  function handlePluginJSConcatenation(req, res, pathname) {
    const pluginName = pathname.split('/')[2];
    const pluginDir = path.join(staticDirs[0], 'plugins', pluginName);
    if (fs.existsSync(pluginDir)) {
      const files = fs.readdirSync(pluginDir).filter(f => f.endsWith('.js'));
      let content = '';
      files.forEach(file => {
        const filePath = path.join(pluginDir, file);
        const fileContent = fs.readFileSync(filePath, 'utf8');
        content += fileContent + '\n';
      });
      res.writeHead(200, { 'Content-Type': 'application/javascript' });
      res.end(content);
    } else {
      res.writeHead(404, { 'Content-Type': 'text/plain' });
      res.end('404 Not Found');
    }
  }

  loadStaticFiles();
  console.log('Loaded files:', loadedFiles);

  http.createServer(handleRequest).listen(port, '127.0.0.1', () => {
    console.log(`Server running at http://localhost:${port}/`);
  });
}

